let slideList=document.querySelector(".slider__list"),slideItems=slideList.querySelectorAll(".slider__item"),sliderControl=document.querySelector(".slider__toggles"),sliderPrevious=sliderControl.querySelector(".slider__toggle--previous"),sliderForward=sliderControl.querySelector(".slider__toggle--forward"),slideIndex=1,showSlide=function(e){e<1?slideIndex=slideItems.length:e>slideItems.length&&(slideIndex=1);for(let e=0;e<slideItems.length;e++)slideItems[e].classList.add("slider__item--hidden");slideItems[slideIndex-1].classList.remove("slider__item--hidden")},changeSlide=function(e){showSlide(slideIndex+=e)};sliderPrevious.addEventListener("click",(function(){changeSlide(-1)})),sliderForward.addEventListener("click",(function(){changeSlide(1)}));